<?php class GuestsController extends Controller {			public function homepage()	{			$i = false;		$picture = "<a href=".BASE_URL."> <img class= 'img1' src='".BASE_URL."public/img/Lewis_Black.jpg'> </a>";		$this->set('picture',$picture);		$description = "description";		$this->set('description',$description);		$optredens = $this->_model->select_all_optredens();		//var_dump($optredens);		$show_table = '';		foreach ($optredens as $value)		{			if($i == false){				$i = true;				$show_table .= "<tr>									<td class='left'><a href=".BASE_URL."> <img class= 'img1' src='".BASE_URL."public/img/".$value["Optreden"]["foto"]."'> </a></td>							    ";			}			else{				$i = false;				$show_table .= "									<td class='right'><a href=".BASE_URL."> <img class= 'img1' src='".BASE_URL."public/img/".$value["Optreden"]["foto"]."'> </a></td>									</tr>";			}		}		$this->set('show_optredens', $show_table);	}	public function login()	{		if(isset($_POST['submit'])){			if (!empty($_POST['email']) && !empty($_POST['password']))			{							$user = $this->_model->select_user_from_login($_POST);				if ( sizeof($user) > 0)				{					$_SESSION['userrole'] = $user[0]['Userrole']['userrole'];					switch ($user[0]['Userrole']['userrole'])					{						case "administrator":							$sendToPage = BASE_URL."administrators/adminhomepage";						break;						case "user":							$sendToPage = BASE_URL."users/homepage";						break;					}					$header = "";					header("refresh:1;url=".$sendToPage);				}				else				{					$header = "Wachtwoord en/of gebruikersnaam niet juist.";				}			}			else			{				$header = "U heeft een van de velden niet ingevuld.";			}			$this->set("header", $header);		}		else{			$header = "";			$this->set("header", $header);		}	}}?>