<?php class UsersController extends Controller {			public function homepage()	{				$this->set('header', 'Welkom bij manifesto Driebruggen');	}	public function viewall_users()	{		$this->set('header', 'Geregistreerde gebruikers');		$users = $this->_model->select_all();		//var_dump($users);		$show_table = '';		foreach ($users as $value)		{			$show_table .= "<tr>								<td>".$value['User']['user_id']."</td>								<td>".$value['User']['voornaam']."</td>								<td>".$value['User']['achternaam']."</td>								<td>".$value['User']['email']."</td>								<td>".$value['User']['wachtwoord']."</td>								<td>".$value['Userrole']['userrole']."</td>						    </tr>";		}		$this->set('show_users', $show_table);	}}?>