<?php class UsersController extends Controller {			public function homepage()	{			$header = "Homepage";		$this->set('header', $header);	}	public function logout()	{		if ( isset($_SESSION['userrole'])) {			session_destroy();			header("location:../guests/homepage");		}	}	public function bestel($id=0)	{		$optreden = $this->_model->select_optreden_by_id($id);		$title = "<h2>".$optreden[0]['Optreden']['naam']."</h2>";		$prijs = "&#8364; ".$optreden[0]['Optreden']['kaart_prijs'];		$header = "<h1>reservering</h1>";		$this->set('header',$header);		$this->set('title',$title);		$this->set('prijs',$prijs);		$this->set('id',$id);		if (isset($_POST['amount'])) {			if ($this->check_login()){				$this->_model->insert_into_bestelling($_POST);				header("refresh:0;url=".BASE_URL."users/bestel/".$id."");			}			else			{				$header = "<h1>U bent niet ingelogd,<br> u wordt doorgestuurd naar de login pagina</h1>";				header("refresh:4;url=".BASE_URL."guests/login");			}		}		$this->set('header',$header);	}	public function check_login()	{		if (isset($_SESSION['user_id']))		{			return true;		}		else		{			return false;		}	}}?>